pragma solidity 0.8.22;

import {IToken} from "./interfaces/IToken.sol";
import {
    IStealthSwapHelper,
    IStealthereum
} from "./interfaces/IStealthSwapHelper.sol";

/// @title Stealth Swap Helper
/// @author mrs kzg.eth
/// @notice router contract for performing an ERC20 token swap before sending the result of the swap to a stealth address
contract StealthSwapHelper is IStealthSwapHelper {

    error ArrayLengthMismatch();
    error NoSwapOutput();
    error WrongMsgValue();
    error SwapCallFailed();

    address internal ETH = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    IStealthereum public immutable stealthereum;

    constructor(address _stealthereum) {
        stealthereum = IStealthereum(_stealthereum);
    }

    /// @inheritdoc IStealthSwapHelper
    function stealthSwap(
        StealthSwap calldata swap
    ) external payable {
        uint256 value;
        if (swap.inputToken == ETH) {
            if (swap.inputAmount + swap.nativeTransfer != msg.value) revert WrongMsgValue();
            value = swap.inputAmount;
        } else {
            IToken(swap.inputToken).transferFrom(msg.sender, address(this), swap.inputAmount);
            IToken(swap.inputToken).approve(swap.swapRouter, swap.inputAmount);
        }

        (bool success,) = swap.swapRouter.call{value: value}(swap.swapPayload);
        if (!success) revert SwapCallFailed();

        uint256 outputAmount = IToken(swap.outputToken).balanceOf(address(this));
        if (outputAmount == 0) revert NoSwapOutput();

        address[] memory tokens = new address[](1);
        tokens[0] = swap.outputToken;
        uint256[] memory values = new uint256[](1);
        values[0] = outputAmount;

        IToken(swap.outputToken).approve(address(stealthereum), outputAmount);

        stealthereum.stealthTransfer{value: swap.nativeTransfer}(
            IStealthereum.StealthTransfer({
                schemeId: swap.schemeId,
                stealthAddress: swap.stealthAddress,
                ephemeralPubkey: swap.ephemeralPubkey,
                viewTag: swap.viewTag,
                tokens: tokens,
                values: values,
                extraMetadata: swap.extraMetadata
            })
        );
    }

    /// @inheritdoc IStealthSwapHelper
    function stealthSwapAndBatch(
        StealthSwap calldata swap,
        IStealthereum.StealthTransfer calldata transferData,
        uint256 transferValueETH
    ) external payable {
        uint256 swapValue;
        if (swap.inputToken == ETH) {
            if (swap.inputAmount + swap.nativeTransfer + transferValueETH != msg.value) revert WrongMsgValue();
            swapValue = swap.inputAmount;
        } else {
            IToken(swap.inputToken).transferFrom(msg.sender, address(this), swap.inputAmount);
            IToken(swap.inputToken).approve(swap.swapRouter, swap.inputAmount);
        }

        (bool success,) = swap.swapRouter.call{value: swapValue}(swap.swapPayload);
        if (!success) revert SwapCallFailed();

        uint256 outputAmount = IToken(swap.outputToken).balanceOf(address(this));
        if (outputAmount == 0) revert NoSwapOutput();

        for (uint256 i = 0; i < transferData.tokens.length; i++) {
            address token = transferData.tokens[i];
            uint256 v = transferData.values[i];
            IToken(token).transferFrom(msg.sender, address(this), v);
            IToken(token).approve(address(stealthereum), v);
        }

        address[] memory tokens = new address[](1);
        tokens[0] = swap.outputToken;
        uint256[] memory values = new uint256[](1);
        values[0] = outputAmount;

        IStealthereum.StealthTransfer[] memory transfersData = new IStealthereum.StealthTransfer[](2);
        transfersData[0] = IStealthereum.StealthTransfer({
            schemeId: swap.schemeId,
            stealthAddress: swap.stealthAddress,
            ephemeralPubkey: swap.ephemeralPubkey,
            viewTag: swap.viewTag,
            tokens: tokens,
            values: values,
            extraMetadata: swap.extraMetadata
        });
        transfersData[1] = transferData;

        uint256[] memory msgvalues = new uint256[](2);
        msgvalues[0] = swap.nativeTransfer;
        msgvalues[1] = transferValueETH;

        IToken(swap.outputToken).approve(address(stealthereum), outputAmount);

        stealthereum.batchStealthTransfers{value: swap.nativeTransfer+transferValueETH}(
            transfersData,
            msgvalues
        );
    }

    receive() external payable {}
}","keccak256":"0x087b7bde88aa8f3442b9628d338923c4e66b5a6cf0efc690da1bd35576126db4","license":"MIT"},"contracts/interfaces/IStealthSwapHelper.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import {IStealthereum} from "./IStealthereum.sol";

interface IStealthSwapHelper {
    /// @notice The StealthSwap struct
    /// @param schemeId id for the stealth address cryptographic scheme (1 = secp256k1 with view tags)
    /// @param stealthAddress the stealth address to transfer to
    /// @param ephemeralPubkey the ephemeral pubkey used to create the stealth address (and used by recipient to find the private key)
    /// @param viewTag the view tag for quicker scanning
    /// @param extraMetadata any extra data to append to the metadata
    /// @param inputToken input token address, for swap (use 0xeeee....) for native ETH
    /// @param inputAmount amount of inputToken to swap
    /// @param outputToken address of output token for swap
    /// @param swapRouter target contract to call for swap action
    /// @param swapPayload to call on target contract for swap action
    /// @param nativeTransfer amount of native ETH to transfer to the stealth address receiving the output of the swap
    struct StealthSwap {
        uint256 schemeId;
        address stealthAddress;
        bytes ephemeralPubkey;
        uint8 viewTag;
        bytes extraMetadata;
        address inputToken;
        uint256 inputAmount;
        address outputToken;
        address swapRouter;
        bytes swapPayload;
        uint256 nativeTransfer;
    }

    /// @notice swap ETH or an ERC20 into an ERC20 before sending it to a stealth address
    /// @param swap a struct containing swap info and stealth transfer info, see StealthSwap struct.
    function stealthSwap(
        StealthSwap calldata swap
    ) external payable;

    /// @notice batch a stealth swap operation with a second stealth transfer
    /// @param swap a struct containing swap info and stealth transfer info, see StealthSwap struct.
    /// @param transferData a struct containing stealth transfer params, see StealthTransfer struct.
    /// @param transferValueETH amount of native token to send in the second stealth transfer.
    /// @dev Meant for doing a stealth swap and then clearing the rest of the used stealth address to a new "change address" all in one batched op
    function stealthSwapAndBatch(
        StealthSwap calldata swap,
        IStealthereum.StealthTransfer calldata transferData,
        uint256 transferValueETH
    ) external payable;

}","keccak256":"0xddb09d0df7c1c997fae1697e658cf4d0a890156c33eba1b471db7b846a750f84","license":"MIT"},"contracts/interfaces/IStealthereum.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

interface IStealthereum {

    error WrongMsgValue();
    error MalformattedMetadata();
    error NativeTransferFailed();
    error ArrayLengthMismatch();

    /// @notice The StealthTransfer struct
    /// @param schemeId id for the stealth address cryptographic scheme (1 = secp256k1 with view tags)
    /// @param stealthAddress the stealth address to transfer to
    /// @param ephemeralPubkey the ephemeral pubkey used to create the stealth address (and used by recipient to find the private key)
    /// @param viewTag the view tag for quicker scanning
    /// @param tokens list of tokens to transfer to the stealth address (supports both ERC20 and ERC721)
    /// @param values the amount (or tokenId, in the case of ERC721) to transfer per token address
    /// @param extraMetadata any extra data to append to the metadata
    struct StealthTransfer {
        uint256 schemeId; 
        address stealthAddress;
        bytes ephemeralPubkey;
        uint8 viewTag;
        address[] tokens;
        uint256[] values;
        bytes extraMetadata;
    }

    /// @notice stealth transfer native token and/or any number of ERC20 / ERC721 tokens directly to a stealth address on announcement to chain's ERC5564Announcer
    /// @param transferData a struct containing stealth transfer params, see StealthTransfer struct
    /// @dev Caller must approve `values[i]` for each `tokens[i]` contract before invoking this function.
    /// This function will properly encode metadata on your behalf complying with ERC5564 spec and extending it.
    /// Any non-zero msg.value will be transferred to the stealth address too.
    function stealthTransfer(
        StealthTransfer calldata transferData
    ) external payable;

    /// @notice stealth transfers to multiple stealth addresses in one batched call
    /// @param transfersData list of structs containing containing stealth transfer params, see StealthTransfer struct
    /// @param msgvalues native transfer amounts per StealthTransfer
    /// @dev Caller must approve `values[i]` for each `tokens[i]` contract (in each StealthTransfer) before invoking this function.
    /// This function will properly encode metadata on your behalf compying with ERC5564 suggestions.
    function batchStealthTransfers(
        StealthTransfer[] calldata transfersData,
        uint256[] calldata msgvalues
    ) external payable;

    /// @notice parse metadata to check for valid formatting and what transfers it encodes
    /// @param metadata the bytes of metadata to parse
    /// @return valueETH amount of native ETH transferred
    /// @return tokens list of token transfers
    /// @return values values transferred in token transfers
    /// @return extraDataLen length of custom extra data appended to the standard metadata format
    /// @dev METADATA SPEC: the first (n)(56)+1 bytes of metadata MUST conform to the following format (where `n` is number of native or token transfers).
    /// First byte MUST be a view tag (if no view tag, can be any arbitrary byte but should be 0x00).
    /// IF forwarding the native token (e.g. ETH) next 56 bytes MUST be 24 bytes of 0xee + 32 byte uint matching msg.value.
    /// Next 56 byte chunks MAY start with 0x23b872dd (transferFrom) to be considered a token transfer.
    /// These token transfer 56 byte chunks go 0x23b872dd + 20 byte token address + 32 byte uint of amount/tokenId to transfer.
    /// Once the start of a 56 byte chunk does not start with 0x23b872dd, we consider this unstructured appended data.
    function parseMetadata(
        bytes memory metadata
    ) external pure returns (uint256 valueETH, address[] memory tokens, uint256[] memory values, uint256 extraDataLen);

    /// @notice encode metadata from the list of transfers and any "extra" appended metadata
    /// @param msgvalue amount of native eth transferred
    /// @param viewTag view tag for the stealth transfer
    /// @param tokens list of token addresses (ERC20 or ERC721)
    /// @param values list of values transferred per token
    /// @param extraMetadata any extra data that sender wants to append to the metadata
    /// @return metadata the encoded metadata output
    function getMetadata(
        uint256 msgvalue,
        uint8 viewTag,
        address[] calldata tokens,
        uint256[] calldata values,
        bytes memory extraMetadata
    ) external pure returns (bytes memory metadata);
}","keccak256":"0xe24c3301cfb38df529887b7de6714ac865a0966b9f1289090052ceb548986b83","license":"MIT"},"contracts/interfaces/IToken.sol":{"content":"// SPDX-License-Identifier: MIT
