pragma solidity 0.8.22;

import {IERC5564Announcer} from "./interfaces/IERC5564Announcer.sol";
import {IToken} from "./interfaces/IToken.sol";
import {IStealthereum} from "./interfaces/IStealthereum.sol";

/// @title stealth.ereum
/// @author mrs kzg.eth
/// @notice ERC5564 compliant stealth addresses integration with an extended metadata standard
contract Stealthereum is IStealthereum {

    bytes32 internal constant _ETH_AND_SELECTOR = 0xeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeee0000000000000000;
    bytes32 internal constant _TRANSFER_FROM_SELECTOR = 0x23b872dd00000000000000000000000000000000000000000000000000000000;

    /// @dev chain's canonical ERC5564 announcer singleton contract
    IERC5564Announcer public immutable announcer;

    constructor(address _announcer) {
        announcer = IERC5564Announcer(_announcer);
    }

    /// @inheritdoc IStealthereum
    function stealthTransfer(
        StealthTransfer calldata transferData
    ) external payable {
        bytes memory metadata = getMetadata(
            msg.value,
            transferData.viewTag,
            transferData.tokens,
            transferData.values,
            transferData.extraMetadata
        );

        _doTransfers(
            msg.value,
            msg.sender,
            transferData.stealthAddress,
            transferData.tokens,
            transferData.values
        );

        announcer.announce(
            transferData.schemeId,
            transferData.stealthAddress, 
            transferData.ephemeralPubkey, 
            metadata
        );
    }

    /// @inheritdoc IStealthereum
    function batchStealthTransfers(
        StealthTransfer[] calldata transfersData,
        uint256[] calldata msgvalues
    ) external payable {
        uint256 len = transfersData.length;
        if (msgvalues.length != len) revert ArrayLengthMismatch();
        uint256 startValue = msg.value;
        uint256 endValue;
        for (uint256 i; i < len; i++) {
            bytes memory metadata = getMetadata(
                msgvalues[i],
                transfersData[i].viewTag,
                transfersData[i].tokens,
                transfersData[i].values,
                transfersData[i].extraMetadata
            );

            _doTransfers(
                msgvalues[i],
                msg.sender,
                transfersData[i].stealthAddress,
                transfersData[i].tokens,
                transfersData[i].values
            );

            announcer.announce(
                transfersData[i].schemeId,
                transfersData[i].stealthAddress, 
                transfersData[i].ephemeralPubkey, 
                metadata
            );

            endValue += msgvalues[i];
        }

        if (endValue != startValue) revert WrongMsgValue();
    }

    /// @inheritdoc IStealthereum
    function parseMetadata(
        bytes memory metadata
    ) external pure returns (uint256 valueETH, address[] memory tokens, uint256[] memory values, uint256 extraDataLen) {
        uint256 len = metadata.length;
        if (len < 57) revert MalformattedMetadata();

        bytes32 checkSelector;
        assembly {
            checkSelector := shl(0x40, shr(0x40, mload(add(metadata, 0x21))))
        }

        bool sendsETH = checkSelector == _ETH_AND_SELECTOR;
        if (sendsETH) {
            assembly {
                valueETH := mload(add(metadata, 0x39))
            }
        }

        uint256 n = (len - 1)/56;
        uint256 arrayLen;
        if (n > 1 || !sendsETH) {
            assembly {
                let startPtr := add(metadata, 0x59)
                let max := sub(n, 0x01)
                if iszero(sendsETH) {
                    max := n
                    startPtr := add(metadata, 0x21)
                }
                for {let i := 0x00} lt(i, max) {i := add(i, 0x01)} {
                    switch eq(shl(0xe0, shr(0xe0, mload(add(startPtr, mul(i, 0x38))))), _TRANSFER_FROM_SELECTOR)
                    case 0 {
                        break
                    }
                    default {
                        arrayLen := add(arrayLen, 0x01)
                    }
                }
                let tFree := mload(0x40)
                let tDataPtr := add(tFree, 0x20)
                let vFree := add(tDataPtr, mul(arrayLen, 0x20))
                let vDataPtr := add(vFree, 0x20)
                let tStartPtr := add(startPtr, 0x04)
                let vStartPtr := add(startPtr, 0x18)
                mstore(tFree, arrayLen)
                mstore(vFree, arrayLen)
                for {let j := 0x00} lt(j, arrayLen) {j := add(j, 0x01)} {
                    mstore(add(tDataPtr, mul(j, 0x20)), shr(0x60, mload(add(tStartPtr, mul(j, 0x38)))))
                    mstore(add(vDataPtr, mul(j, 0x20)), mload(add(vStartPtr, mul(j, 0x38))))
                }

                tokens := tFree
                values := vFree

                mstore(0x40, add(tFree, add(mul(arrayLen, 0x40), 0x40))) // update free memory pointer
            }
        }

        uint256 start = sendsETH ? 57+arrayLen*56 : 1+arrayLen*56;
        extraDataLen = metadata.length - start;
    }

    /// @inheritdoc IStealthereum
    function getMetadata(
        uint256 msgvalue,
        uint8 viewTag,
        address[] calldata tokens,
        uint256[] calldata values,
        bytes memory extraMetadata
    ) public pure returns (bytes memory metadata) {
        uint256 len = tokens.length;
        if (len != values.length) revert ArrayLengthMismatch();

        bool sendsETH = msgvalue > 0;
        uint256 metadataLen = sendsETH ? 56*(len+1)+1 : 56*len+1;
        bytes memory data = new bytes(metadataLen);

        assembly {
            let startPtr := add(data, 0x21)
            let n := div(sub(metadataLen, 0x01), 0x38)
            mstore8(add(data, 0x20), viewTag)
            if gt(sendsETH, 0) {
                mstore(add(data, 0x21), _ETH_AND_SELECTOR)
                mstore(add(data, 0x39), msgvalue)

                startPtr := add(data, 0x59)
                n := sub(n, 0x01)
            }
            for {let i := 0x00} lt(i, n) {i := add(i, 0x01)} {
                let k := add(startPtr, mul(i, 0x38))
                mstore(k, _TRANSFER_FROM_SELECTOR)
                calldatacopy(0x00, add(tokens.offset, mul(i, 0x20)), 0x20)
                calldatacopy(0x20, add(values.offset, mul(i, 0x20)), 0x20)
                mstore(add(k, 0x04), shl(0x60, mload(0x00)))
                mstore(add(k, 0x18), mload(0x20))
            }
        }

        metadata = bytes.concat(data, extraMetadata);
    }

    function _doTransfers(
        uint256 msgvalue,
        address msgsender,
        address stealthAddress,
        address[] memory tokens,
        uint256[] memory values
    ) internal {
        for (uint256 i = 0; i < tokens.length; i++) {
            IToken(tokens[i]).transferFrom(msgsender, stealthAddress, values[i]);
        }

        if (msgvalue > 0) {
            bool success;
            assembly {
                success := call(gas(), stealthAddress, msgvalue, 0, 0, 0, 0)
            }
            if (!success) revert NativeTransferFailed();
        }
    }

    receive() external payable {}
}","keccak256":"0x7bd82b2168c9cc6d1f4081f8bee9334830fb917bf3f2fc01b096f2c3247d934e","license":"MIT"},"contracts/interfaces/IERC5564Announcer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

/// NOTE very slight adaptation to code found here https://eips.ethereum.org/EIPS/eip-5564

/// @notice Interface for announcing when something is sent to a stealth address.
interface IERC5564Announcer {
  /// @dev Emitted when sending something to a stealth address.
  /// @dev See the `announce` method for documentation on the parameters.
  event Announcement (
    uint256 indexed schemeId, 
    address indexed stealthAddress, 
    address indexed caller, 
    bytes ephemeralPubKey, 
    bytes metadata
  );

  /// @dev Called by integrators to emit an `Announcement` event.
  /// @param schemeId The integer specifying the applied stealth address scheme.
  /// @param stealthAddress The computed stealth address for the recipient.
  /// @param ephemeralPubKey Ephemeral public key used by the sender.
  /// @param metadata An arbitrary field MUST include the view tag in the first byte.
  /// Besides the view tag, the metadata can be used by the senders however they like, 
  /// but the below guidelines are recommended:
  /// The first byte of the metadata MUST be the view tag.
  /// - When sending/interacting with the native token of the blockchain (cf. ETH), the metadata SHOULD be structured as follows:
  ///     - Byte 1 MUST be the view tag, as specified above.
  ///     - Bytes 2-5 are `0xeeeeeeee`
  ///     - Bytes 6-25 are the address 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE.
  ///     - Bytes 26-57 are the amount of ETH being sent.
  /// - When interacting with ERC-20/ERC-721/etc. tokens, the metadata SHOULD be structured as follows:
  ///   - Byte 1 MUST be the view tag, as specified above.
  ///   - Bytes 2-5 are a function identifier. When a function selector (e.g.
  ///     the first (left, high-order in big-endian) four bytes of the Keccak-256
  ///     hash of the signature of the function, like Solidity and Vyper use) is
  ///     available, it MUST be used.
  ///   - Bytes 6-25 are the token contract address.
  ///   - Bytes 26-57 are the amount of tokens being sent/interacted with for fungible tokens, or
  ///     the token ID for non-fungible tokens.
  function announce (
    uint256 schemeId, 
    address stealthAddress, 
    bytes memory ephemeralPubKey, 
    bytes memory metadata
  ) external;
}","keccak256":"0x694b047c5dac5fb5490e4b7c9357b96e903d3c6de506982e8266ab64e14d09c5","license":"MIT"},"contracts/interfaces/IStealthereum.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

interface IStealthereum {

    error WrongMsgValue();
    error MalformattedMetadata();
    error NativeTransferFailed();
    error ArrayLengthMismatch();

    /// @notice The StealthTransfer struct
    /// @param schemeId id for the stealth address cryptographic scheme (1 = secp256k1 with view tags)
    /// @param stealthAddress the stealth address to transfer to
    /// @param ephemeralPubkey the ephemeral pubkey used to create the stealth address (and used by recipient to find the private key)
    /// @param viewTag the view tag for quicker scanning
    /// @param tokens list of tokens to transfer to the stealth address (supports both ERC20 and ERC721)
    /// @param values the amount (or tokenId, in the case of ERC721) to transfer per token address
    /// @param extraMetadata any extra data to append to the metadata
    struct StealthTransfer {
        uint256 schemeId; 
        address stealthAddress;
        bytes ephemeralPubkey;
        uint8 viewTag;
        address[] tokens;
        uint256[] values;
        bytes extraMetadata;
    }

    /// @notice stealth transfer native token and/or any number of ERC20 / ERC721 tokens directly to a stealth address on announcement to chain's ERC5564Announcer
    /// @param transferData a struct containing stealth transfer params, see StealthTransfer struct
    /// @dev Caller must approve `values[i]` for each `tokens[i]` contract before invoking this function.
    /// This function will properly encode metadata on your behalf complying with ERC5564 spec and extending it.
    /// Any non-zero msg.value will be transferred to the stealth address too.
    function stealthTransfer(
        StealthTransfer calldata transferData
    ) external payable;

    /// @notice stealth transfers to multiple stealth addresses in one batched call
    /// @param transfersData list of structs containing containing stealth transfer params, see StealthTransfer struct
    /// @param msgvalues native transfer amounts per StealthTransfer
    /// @dev Caller must approve `values[i]` for each `tokens[i]` contract (in each StealthTransfer) before invoking this function.
    /// This function will properly encode metadata on your behalf compying with ERC5564 suggestions.
    function batchStealthTransfers(
        StealthTransfer[] calldata transfersData,
        uint256[] calldata msgvalues
    ) external payable;

    /// @notice parse metadata to check for valid formatting and what transfers it encodes
    /// @param metadata the bytes of metadata to parse
    /// @return valueETH amount of native ETH transferred
    /// @return tokens list of token transfers
    /// @return values values transferred in token transfers
    /// @return extraDataLen length of custom extra data appended to the standard metadata format
    /// @dev METADATA SPEC: the first (n)(56)+1 bytes of metadata MUST conform to the following format (where `n` is number of native or token transfers).
    /// First byte MUST be a view tag (if no view tag, can be any arbitrary byte but should be 0x00).
    /// IF forwarding the native token (e.g. ETH) next 56 bytes MUST be 24 bytes of 0xee + 32 byte uint matching msg.value.
    /// Next 56 byte chunks MAY start with 0x23b872dd (transferFrom) to be considered a token transfer.
    /// These token transfer 56 byte chunks go 0x23b872dd + 20 byte token address + 32 byte uint of amount/tokenId to transfer.
    /// Once the start of a 56 byte chunk does not start with 0x23b872dd, we consider this unstructured appended data.
    function parseMetadata(
        bytes memory metadata
    ) external pure returns (uint256 valueETH, address[] memory tokens, uint256[] memory values, uint256 extraDataLen);

    /// @notice encode metadata from the list of transfers and any "extra" appended metadata
    /// @param msgvalue amount of native eth transferred
    /// @param viewTag view tag for the stealth transfer
    /// @param tokens list of token addresses (ERC20 or ERC721)
    /// @param values list of values transferred per token
    /// @param extraMetadata any extra data that sender wants to append to the metadata
    /// @return metadata the encoded metadata output
    function getMetadata(
        uint256 msgvalue,
        uint8 viewTag,
        address[] calldata tokens,
        uint256[] calldata values,
        bytes memory extraMetadata
    ) external pure returns (bytes memory metadata);
}","keccak256":"0xe24c3301cfb38df529887b7de6714ac865a0966b9f1289090052ceb548986b83","license":"MIT"},"contracts/interfaces/IToken.sol":{"content":"// SPDX-License-Identifier: MIT
